div.script:
        document.addEventListener('DOMContentLoaded', function () {
        if (checkSessionStorageAvailability()) {
        var url;
        if (location.href.match(/\/Bet\/Index\/\d+|\/GameBet/)) {
        url = location.href.match(/\/Bet\/Index\/\d+|\/GameBet/)[0];
        if (url == "/GameBet") {
        url += "/Lobby"
        }
        }

        if (url) {
        $(".lotteryGameMenu").each(function (index, item) {
        if (url == $(item).attr("href")) {
        $(item).addClass("active");
        return false;
        }
        });
        }
        }
        }, false);

body.script:
      $(function () {
      $('nav#menu_right').show().mmenu({
      extensions: ["pagedim-black", "effect-menu-slide", "effect-listitems-slide", "border-full"],
      offCanvas: { position: "right", },
      navbar: false,
      });
      $('[href="#menu_left"]').click(function (e) {
      e.preventDefault();
      $('#menu_left').slideToggle();
      $(this).children(".fa").toggle();
      });

      if (!/^((?!chrome|android).)*(safari|iPhone)/i.test(navigator.userAgent)) {
      $("#toggleFullScreen").show();
      $("#toggleFullScreen").click(function () {
      $(".toggleFullScreen").toggle();
      })
      }


      var $item = null;
      var url = "";
      url = GetMerchantWalletUrl("walletDepositUrl");
      if (url) {
      $item = $("#goDeposit");
      $item.show();
      $item.find("a").attr("href", url);
      }

      url = GetMerchantWalletUrl("walletWithdrawUrl");
      if (url) {
      $item = $("#goWithdraw");
      $item.show();
      $item.find("a").attr("href", url);
      }

      url = GetMerchantWalletUrl("walletUrl");
      if (url) {
      $item = $("#goWallet");
      $item.show();
      $item.find("a").attr("href", url);
      }


      });
